mapper.select:
select * from sys_menu where deleted = 0

	and type = #{type}

order by sort asc

select t3.* from sys_user_role t1 left join sys_role_menu t2 on t1.role_id = t2.role_id left join sys_menu t3 on t2.menu_id = t3.id where t1.user_id = #{userId} and t1.deleted = 0 and t2.deleted = 0 and t3.deleted = 0
and t3.type = #{type}
order by t3.sort asc
